// 2. GLOBAL VARIABLES (The App's Memory)
let currentSubject = "";
let currentQuestionIndex = 0;
let score = 0;
let timerInterval;
let timeLeft = 60; // 60 seconds for testing

// 3. STARTUP LOGIC
function startExam(subject) {
    // Initialize variables
    currentSubject = subject;
    currentQuestionIndex = 0;
    score = 0;
    timeLeft = 60; 

    // UI Reset: Hide menu, show exam screen
    document.getElementById('welcome-screen').style.display = 'none';
    document.getElementById('results-screen').style.display = 'none';
    document.getElementById('exam-screen').style.display = 'block';

    // Start the engine
    showQuestion();
    startTimer();
}

// 4. CORE FUNCTION: SHOW QUESTION
function showQuestion() {
    const questionList = database[currentSubject];
    const qData = questionList[currentQuestionIndex];
    
    // Update Question Text
    document.getElementById('q-text').innerText = `Question ${currentQuestionIndex + 1}: ${qData.q}`;
    
    // Target the Options Box
    const optionsBox = document.getElementById('options-box');
    optionsBox.innerHTML = ""; // Clear old buttons

    // Create buttons for each option
    qData.options.forEach((optionText, i) => {
        const btn = document.createElement('button');
        btn.innerText = optionText;
        btn.className = "option-btn";
        
        // Link the button to the answer handler
        btn.onclick = () => handleAnswer(i);
        
        optionsBox.appendChild(btn);
    });
}

// 5. CORE FUNCTION: HANDLE ANSWER
 // Part A: When the user clicks an option
function handleAnswer(selectedIndex) {
    const qData = database[currentSubject][currentQuestionIndex];
    const expBox = document.getElementById('explanation-box');
    const feedback = document.getElementById('feedback-text');
    const expContent = document.getElementById('exp-content');
    
    // Disable all buttons so they can't click twice
    const buttons = document.querySelectorAll('.option-btn');
    buttons.forEach(btn => btn.disabled = true);

    // Check if correct
    if (selectedIndex === qData.a) {
        score++;
        feedback.innerText = "Correct! ✅";
        feedback.style.color = "green";
        expBox.style.backgroundColor = "#d4edda"; // Light green
    } else {
        feedback.innerText = "Wrong! ❌";
        feedback.style.color = "red";
        expBox.style.backgroundColor = "#f8d7da"; // Light red
    }

    // Show the explanation from your database
    expContent.innerText = qData.exp;
    expBox.style.display = "block"; 
}

// Part B: When the user clicks the "Next Question" button
function nextQuestion() {
    // Hide the explanation box for the next round
    document.getElementById('explanation-box').style.display = "none";
    
    currentQuestionIndex++;

    if (currentQuestionIndex < database[currentSubject].length) {
        showQuestion();
    } else {
        finishExam();
    }
}   

// 6. RESULTS LOGIC
function finishExam() {
    clearInterval(timerInterval); // Stop the clock
    
    document.getElementById('exam-screen').style.display = 'none';
    document.getElementById('results-screen').style.display = 'block';
    
    const total = database[currentSubject].length;
    document.getElementById('final-score').innerText = score;
    document.getElementById('percentage-text').innerText = `You scored ${Math.round((score/total)*100)}%`;
}

// 7. UTILITY: TIMER & NAVIGATION
function startTimer() {
    timerInterval = setInterval(() => {
        timeLeft--;
        document.getElementById('timer').innerText = `Time: ${timeLeft}s`;
        if (timeLeft <= 0) {
            finishExam();
        }
    }, 1000);
}

function goToMenu() {
    clearInterval(timerInterval);
    document.getElementById('exam-screen').style.display = 'none';
    document.getElementById('results-screen').style.display = 'none';
    document.getElementById('welcome-screen').style.display = 'block';
}
